# Получаем JWT от Finam
def get_finam_jwt(secret: str) -> str:
    response = requests.post("https://api.finam.ru/v1/sessions", json={"secret": secret})
    response.raise_for_status()
    return response.json()["token"]

# Делаем запрос к аккаунту
def get_account_info(jwt_token: str):
    headers = {"Authorization": jwt_token}  # или Authorization: JWT <token> в зависимости от документации
    response = requests.get(f"https://api.finam.ru/v1/accounts/{ACCOUNT_ID}", headers=headers)
    response.raise_for_status()
    return response.json()

if __name__ == "__main__":
    jwt_token = get_finam_jwt(FINAM_SECRET)
    account_info = get_account_info(jwt_token)
    print(account_info)